type EventOptionsMap = {
  // appLoaded: {}

  // WELCOME
  welcomeShown: {};
  welcomeStep: { welcomeStepName: string };
  welcomeCompleted: {};

  // TUTORIAL
  tutorialStep: { step: number };
  tutorialCompleted: {};
